insert into company (id, name, description, visa_sponsor, created_at, updated_at, website, logo_url, jobs_page) values (4, 'Elmo', 'Founded in 2002, ELMO (ASX:ELO) is Australia and New Zealand’s only integrated cloud HR, payroll and rostering / time & attendance solution, offering you:

